BASE_WEAPON_DMG = (BASE_HP + BASE_ARMOR_ABSORB) / (1 - BASE_ARMOR_DEF * 0.001)

GRADE_DMG_MOD = {
		0: 0.5, # trash
		1: 1.0, # common
		2: 1.1, # uncommon
		3: 1.2, # rare
		4: 1.3, # special
		5: 1.4, # uniq
		}

AMMO_TYPE_DMG_MOD = {
		'FMJ': 1.0,
		'AP': 0.8,
		'HP': 1.2,
		'SL': 0.5,
		}

def mkDmg(average_percent_damage, delta, ammo_type=None, grade=1):
	if ammo_type == None:
		ammo_type = 'FMJ'
	dmg_average = BASE_HP * average_percent_damage * 0.01 * GRADE_DMG_MOD[grade] * AMMO_TYPE_DMG_MOD[ammo_type]
	dmg_delta = BASE_HP * delta * 0.01 * GRADE_DMG_MOD[grade] * AMMO_TYPE_DMG_MOD[ammo_type]
	return [dmg_average - dmg_delta, dmg_average + dmg_delta]

def mkItemCopy(item, iid, **kwargs):
	newItem = {}
	newItem.update(item, **kwargs)
	newItem['TypeID'] = iid
	return newItem

